mapper.update:
UPDATE member
SET member_name = #{nameInput}, member_birthday = #{yearInput}, member_address = #{addressInput}
WHERE member_no = #{member_no}
;

UPDATE brief_history
SET brief_history_education = #{educationInput},
	brief_history_career = #{careerInput},
	brief_history_certificate = #{certificateInput},
	brief_history_upload_path = #{imageAddress}
WHERE member_no = 1
;

UPDATE tech
SET tech_delete_yn = 0
WHERE tech_no = #{skillName}
AND member_no = #{member_no}
AND tech_category_no = #{categoryName}

mapper.select:
SELECT education_no, education_etc AS etc, education_name AS name, education_department AS department, education_status AS status, education_upload_path AS imageAddress,
DATE_FORMAT(education_startdate, '%Y-%m-%d') AS startdate, DATE_FORMAT(education_enddate, '%Y-%m-%d') AS enddate
DATE_FORMAT(education_startdate, '%Y.%m') AS startdate, DATE_FORMAT(education_enddate, '%Y.%m') AS enddate
FROM education WHERE education_delete_yn = 1 AND member_no = #{member_no}
AND education_no = #{educationNo}
ORDER BY education_no DESC

mapper.update:
UPDATE education
SET education_delete_yn = 0
WHERE member_no = #{member_no}
AND education_no = #{educationNo}

UPDATE education
SET education_name = #{nameInput}, 
	 education_department = #{departmentInput},
	 education_status = #{status},
	 education_startdate = #{admissionInput},
	 education_enddate = #{graduatedInput},
	 education_etc = #{etcInput},
	 education_upload_path = #{imageAddress}
WHERE member_no = #{member_no}
AND education_no = #{educationNo}

mapper.select:
SELECT company_no, company_name, company_upload_path AS imageAddress,
DATE_FORMAT(company_startdate, '%Y-%m-%d') AS startdate, DATE_FORMAT(company_enddate, '%Y-%m-%d') AS enddate
DATE_FORMAT(company_startdate, '%Y.%m') AS startdate, DATE_FORMAT(company_enddate, '%Y.%m') AS enddate, ROUND(TIMESTAMPDIFF(DAY, company_startdate, company_enddate) / 30) AS cha
FROM company WHERE company_delete_yn = 1 AND member_no = #{member_no}
AND company_no = #{companyNo}
ORDER BY company_no DESC

mapper.update:
UPDATE company
SET company_name = #{nameInput}, company_startdate = #{joinInput}, company_enddate = #{leaveInput}, company_upload_path = #{imageAddress}
WHERE company_no = #{companyNo}
AND member_no = #{member_no}

UPDATE company
SET company_delete_yn = 0
WHERE company_no = #{companyNo}
AND member_no = #{member_no}

mapper.select:
SELECT career_no, company_no, career_responsibility, career_description, career_department FROM career WHERE career_delete_yn = 1
AND career_no = #{careerNo}
ORDER BY career_no DESC

mapper.update:
UPDATE career 
SET career_responsibility=#{taskInput}, career_description=#{careerInput}, career_department=#{departmentInput}
WHERE career_no = #{careerNo}

UPDATE career 
SET career_delete_yn = 0
WHERE career_no = #{careerNo}

mapper.select:
SELECT academy_no, academy_course AS course, academy_content AS content, academy_academy AS academy, academy_status_yn AS status,
DATE_FORMAT(academy_startdate, '%Y-%m-%d') AS startdate, DATE_FORMAT(academy_enddate, '%Y-%m-%d') AS enddate,
DATE_FORMAT(academy_startdate, '%Y.%m') AS startdate, DATE_FORMAT(academy_enddate, '%Y.%m') AS enddate,
academy_upload_path AS imageAddress FROM academy WHERE academy_delete_yn = 1 AND member_no = #{member_no}
AND academy_no = #{academyNo}
ORDER BY academy_no DESC

SELECT certificate_no AS cert_no, certificate_name AS cert_name, certificate_organization AS cert_organization, certificate_pass_yn AS cert_pass, certificate_upload_path AS imageAddress,
DATE_FORMAT(certificate_issue_date, '%Y-%m-%d') AS cert_date
DATE_FORMAT(certificate_issue_date, '%Y.%m') AS cert_date
FROM certificate WHERE certificate_delete_yn = 1 AND member_no = #{member_no}
AND certificate_no = #{certificateNo}
ORDER BY cert_no DESC

